var input = ``;



//Return the index of the first unique string of length "len" in string "str" 
function uniqueStringIndex(str,len){
  var recent = [];
  
  for(var a = 0;a < len;a ++){
    recent[a] = str.charAt(a);
  }
  
  for(var a = len;a < str.length;a ++){
    
    var dups = false;
    
    for(var b = 0;b < len;b ++){
      for(var c = b+1;c < len;c ++){
        
        if(recent[b] == recent[c]){
          dups = true;
        }
        
      }
    }
    
    if(!dups){
      return a;
    }else{
      
      recent.shift();
      
      recent.push(str.charAt(a));
      
    }
    
  }
}



console.log("Result: ",uniqueStringIndex(input,4));
console.log("Result: ",uniqueStringIndex(input,14));









